package com.dev.recyclerview

import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.recyclerview.widget.DividerItemDecoration
import androidx.recyclerview.widget.LinearLayoutManager
import com.dev.recyclerview.adapters.AnimalAdapter
import com.dev.recyclerview.models.Animal
import kotlinx.android.synthetic.main.activity_main.*

class MainActivity : AppCompatActivity(), AnimalAdapter.OnAnimalClickListener {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        setupRecyclerView()
    }

    private fun setupRecyclerView(){
        recyclerView.layoutManager = LinearLayoutManager(this)
        recyclerView.addItemDecoration(DividerItemDecoration(this, DividerItemDecoration.VERTICAL))
        val animalList = listOf(
            Animal(
                "Dog",
                "data:image/jpeg;base64,"
            ),
            Animal(
                "Wolf",
                "https://www.nwf.org/-/media/NEW-WEBSITE/Shared-Folder/Wildlife/Mammals/mammal_gray-wolf_600x300.ashx"
            ),
            Animal(
                "Wolf dog",
                "https://www.theyucatantimes.com/wp-content/uploads/2019/10/calupoh-10-1024x680-e1570208508658.jpg"
            )
        )
        recyclerView.adapter = AnimalAdapter(this, animalList, this)
    }

    override fun onImageClick(image: String) {
       val intent = Intent(this, ImageDetail::class.java)
        intent.putExtra("imageUrl", image)
        startActivity(intent)
    }

    override fun onItemClick(name: String) {
        Toast.makeText(this, "El animal es: $name.", Toast.LENGTH_LONG).show()
    }
}